    local function AddSlider(parent, text, min, max, default, callback)
        local Container = Instance.new("Frame", parent)
        Container.Size = UDim2.new(1, -10, 0, 50)
        Container.BackgroundTransparency = 1
        Container.BorderSizePixel = 0
        Container.ZIndex = 4

